import { useState } from 'react';
import './App.css';
import Navigation from './components/Navigation';
import MoviesList from './components/MovieList.js';
import {moviesData} from './Data'
import AddMovie from './components/AddMovie.js';
import Filter from './components/Filter.js';
import {Router } from 'react-router-dom';
import Contact from './pages/Contact';
import Home from './pages/Home';
import About from './pages/About';
import ErrorPage from './pages/ErrorPage';






function App() {
  const [ movies, setMovies] = useState(moviesData)

const[inputSearch,setInputSearch]= useState("")
const[newRate,setNewRate]=useState(0)

  const add = (newMovie)=> {
    setMovies([...movies,newMovie])
  }

  return (
    <div className="App" style={{backgroundColor: "black"}}>
      <Navigation/> 

      <Router>
        <Router path="/" element={<Home/>}/>
        <Router path="/" element={<About/>}/>
        <Router path="/" element={<Contact/>}/>
        <Router path="/" element={<ErrorPage/>}/>
        <Filter inputSearch={inputSearch} setInputSearch={setInputSearch} newRate={newRate} setNewRate={setNewRate}/>
        add={add}
        movies={movies}
      </Router>


    </div>
  );
}

export default App;
